mapper.select:
select

from express exp
where exp.area_code = #{areaCode}

    and exp.name like concat("%", #{name}, "%")


    and exp.status = #{status}


    and exp.type = #{type}

order by exp.area_code asc, exp.status desc, exp.type desc, exp.id asc

    select *
    from (SELECT count(1) as week_count
    FROM order_info odi
    WHERE DATE_FORMAT(odi.create_time, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m')

        and odi.express_id = #{expressId}

    ) A
    left join (SELECT count(2) as month_count
    FROM order_info odi
    WHERE YEARWEEK(date_format(odi.create_time, '%Y-%m-%d')) = YEARWEEK(now()) 
    and odi.express_id = #{expressId}
) B on 1 = 1

SELECT DATE_FORMAT(odi.express_receive_time, '%Y%u') weeks, count(1) AS total_count, SUM(IFNULL(origin_service_price, 0) - IFNULL(insurance_price, 0)) AS total_money, DATE_FORMAT( (SELECT DATE_ADD(odi.express_receive_time, INTERVAL - WEEKDAY(odi.express_receive_time) DAY)), '%Y%m%d') AS start_date, DATE_FORMAT((SELECT DATE_ADD(odi.express_receive_time, INTERVAL - WEEKDAY(odi.express_receive_time) + 6 DAY)), '%Y%m%d') AS end_date FROM order_info odi left join express e on odi.express_id = e.id WHERE 1 = 1 and odi.express_id is not null and e.type = 1
and odi.area_code = #{areaCode}
and odi.order_status in (${type})
and odi.express_id = #{expressId}
GROUP BY weeks, start_date, end_date ORDER BY weeks DESC

SELECT DATE_FORMAT(odi.express_receive_time, '%Y%u') weeks, count(1) AS total_count, SUM(IFNULL(origin_service_price, 0) - IFNULL(insurance_price, 0)) AS total_money, DATE_FORMAT( (SELECT DATE_ADD(odi.express_receive_time, INTERVAL - WEEKDAY(odi.express_receive_time) DAY)), '%Y%m%d') AS start_date, DATE_FORMAT((SELECT DATE_ADD(odi.express_receive_time, INTERVAL - WEEKDAY(odi.express_receive_time) + 6 DAY)), '%Y%m%d') AS end_date FROM order_info odi left join express e on odi.express_id = e.id WHERE 1 = 1 and odi.express_id is not null and e.type = 1
and odi.area_code = #{areaCode}
and odi.order_status in (${type})
and odi.express_id = #{expressId}
GROUP BY weeks, start_date, end_date ORDER BY weeks DESC
