mapper.select:
select

from good where status = 1 and shop_id=#{shopId}
order by id asc

select , (select name from category c where c.id=g.category_id ) categoryName, p.id coupon_id,p.coupon_name,p.type from good g left join coupon p on g.shop_id = p.shop_id where g.status = 1 and p.status=0
and g.name like #{goodsName}
or g.category_id in (${categoryId})
or g.discount > 0
or p.type in (${proActivities})
order by ${orderBy}

select

from good where status = 1 and shop_id=#{shopId} and category_id=#{categoryId}
order by id asc

select 
from good where status in (0,1) and shop_id = #{shopId}

  and name like concat(concat("%",#{goodName}),"%")


  and good_type_id = #{goodsTypeId}

order by create_time asc
