mapper.select:
select , c.name as category_name, gt.name as goods_type_name from good g LEFT JOIN category c on g.category_id = c.id left join goods_type gt on g.goods_type_id = gt.id where g.status in (0,1) and g.shop_id = #{shopId}
and g.name like concat(concat("%",#{goodName}),"%")
and g.goods_type_id = #{goodsTypeId}
order by g.create_time asc

select

from good where status = 1 and shop_id=#{shopId}
order by id asc

select

from  good g
where g.status = 1 and g.area_code = #{areaCode} and g.category_id = 3

    and g.name like #{goodsName}


    and g.brand_id = #{brandId}


    and g.goods_type_id = #{goodsTypeId}


    and g.scope_id in (${scopeIds})


    and g.pet_category_id = #{petCategoryId}


order by ${orderBy}

select

from good where status = 1 and shop_id=#{shopId} and category_id=#{categoryId}
order by id asc

mapper.update:
update good


        discount=#{discount},


        re_discount=#{reDiscount},


where shop_id = #{shopId}

    and goods_type_id in

        #{item}
