mapper.select:
select 
from good where status in (0,1) and shop_id = #{shopId}

    and name like concat(concat("%",#{goodName}),"%")


    and goods_type_id = #{goodsTypeId}

order by create_time asc

select

from good where status = 1 and shop_id=#{shopId}
order by id asc

select
,
p.id coupon_id,p.cpn_name coupon_name,p.cpn_type type
from  good g left join cpn_info p on g.shop_id = p.shop_id
where g.status = 1

    and g.name like #{goodsName}


    and g.brand_id = #{brandId}


    and g.goods_type_id = #{goodsTypeId}


    and g.scope_id = #{scopeId}


    and g.pet_category_id = #{petCategoryId}


order by ${orderBy}

select

from good where status = 1 and shop_id=#{shopId} and category_id=#{categoryId}
order by id asc
