mapper.select:
select

from order_info
where order_no = #{orderNo}
and user_id = #{userId}

select
from order_info o left join user u on o.user_id = u.id left join order_address oaddr on o.id = oaddr.order_id where shop_id = #{shopId}
and o.order_no like concat('%',#{orderNo},'%')
and u.name like concat('%',#{username},'%')
and oaddr.phone like concat('%',#{phone},'%')
and o.order_status in (${orderStatus})
and o.create_time > #{startDate}
and o.create_time < #{endDate}
group by o.id order by o.create_time desc

select
from order_info o left join shop s on o.shop_id = s.id left join user u on o.user_id = u.id where 1 = 1 and (o.card_id is not null or o.coupon_id is not null)
and o.area_code = #{areaCode}
and o.shop_id = #{shopId}
and s.shop_name like concat('%',#{shopName},'%')
and o.order_no like concat('%',#{orderNo},'%')
and u.name like concat('%',#{username},'%')
and o.create_time > #{startDate}
and o.create_time < #{endDate}
order by o.create_time desc
